class OrganizationCard extends StatelessWidget {
  final String name,imgurl;

  OrganizationCard({required this.name,required this.imgurl});

  @override
  Widget build(BuildContext context) {
    return Container(
      width: 90, // fixed width for uniformity
      margin: const EdgeInsets.only(right: 10.0),
      child: Column(
        children: <Widget>[
          CircleAvatar(
            radius: 30,
            backgroundColor: Colors.blue,
            backgroundImage: NetworkImage(imgurl),
          ),
          SizedBox(height: 5),
          Container(
            width: 110, // fixed width for text container
            child: Text(
              name,
              textAlign: TextAlign.center,
              style: TextStyle(fontSize: 13, color: Colors.grey[700]),
              maxLines: 1, // limit to 2 lines if the name is too long
              // overflow: TextOverflow.ellipsis, // handle overflow text with ellipsis
            ),
          ),
        ],
      ),
    );
  }
}
